// Get tasks array from the webhook data
const tasks = $input.first().json.body.data.tasks;

// Filter alleen tasks waar isPriority = true OF isAndersTask = true
const filteredTasks = tasks.filter(task => 
  task.isPriority === true || task.isAndersTask === true
);

// Return alleen de gefilterde taken
return filteredTasks;

// Get the input data
const webhookData = $('Webhook invullen score card').first().json.body.data;

let llmResult = {};
try {
  const llmRaw = $('anders taken kwalificeren').first().json.content[0].text;
  const llmCleaned = llmRaw.replace(/```json\n?/g, '').replace(/```/g, '').trim();
  const llmParsed = JSON.parse(llmCleaned);
  llmResult = Array.isArray(llmParsed) ? llmParsed[0] : llmParsed;
} catch(e) {
  llmResult = {};
}

// Define savings percentages based on automation score
const savingsPercentages = {
  4: 0.92,
  3: 0.81,
  2: 0.67,
  1: 0.50,
  0: 0
};

// Label assignment based on automation score AND hours combination
function getTaskLabel(automationScore, hours, score) {
  if (automationScore === 4) {
    if (hours > 100) return { label: "Bulls Eye", priority: 1, category: "Absolute Toppers", categoryPriority: 1 };
    if (hours >= 51 && hours <= 100) return { label: "No Brainer", priority: 2, category: "Absolute Toppers", categoryPriority: 1 };
    if (hours >= 26 && hours <= 50) return { label: "Quick Win", priority: 3, category: "Zeer Hoge Prioriteit", categoryPriority: 2 };
    if (hours >= 11 && hours <= 25) return { label: "Efficiency Booster", priority: 6, category: "Hoge Prioriteit", categoryPriority: 3 };
    if (hours <= 10) return { label: "Mini Winst", priority: 11, category: "Net Onder Top 3", categoryPriority: 5 };
  }

  if (automationScore === 3) {
    if (hours > 100) return { label: "Laag Hangend Fruit", priority: 4, category: "Zeer Hoge Prioriteit", categoryPriority: 2 };
    if (hours >= 51 && hours <= 100) return { label: "Tijdwinst Topper", priority: 5, category: "Hoge Prioriteit", categoryPriority: 3 };
    if (hours >= 26 && hours <= 50) return { label: "Solide Keuze", priority: 7, category: "Hoge Prioriteit", categoryPriority: 3 };
    if (hours >= 11 && hours <= 25) return { label: "Slimme Zet", priority: 10, category: "Net Onder Top 3", categoryPriority: 5 };
    if (hours <= 10) return { label: "Kleine Versnelling", priority: 14, category: "Net Onder Top 3", categoryPriority: 5 };
  }

  if (automationScore === 2) {
    if (hours > 100) return { label: "Volume Voordeel", priority: 8, category: "Hoge Prioriteit", categoryPriority: 3 };
    if (hours >= 51 && hours <= 100) return { label: "Substantiele Verbetering", priority: 9, category: "Top 3 Ondergrens", categoryPriority: 4 };
    if (hours >= 26 && hours <= 50) return { label: "Degelijke Optie", priority: 12, category: "Net Onder Top 3", categoryPriority: 5 };
    if (hours >= 11 && hours <= 25) return { label: "Basis Verbetering", priority: 15, category: "Overwegen Bij Specifieke Pijn", categoryPriority: 6 };
    if (hours <= 10) return { label: "Eerste Stapje", priority: 17, category: "Overwegen Bij Specifieke Pijn", categoryPriority: 6 };
  }

  if (automationScore === 1) {
    if (hours > 100) return { label: "Bulk Uitdaging", priority: 13, category: "Net Onder Top 3", categoryPriority: 5 };
    if (hours >= 51 && hours <= 100) return { label: "Complexe Massa", priority: 16, category: "Overwegen Bij Specifieke Pijn", categoryPriority: 6 };
    if (hours >= 26 && hours <= 50) return { label: "Beperkt Potentieel", priority: 18, category: "Lage Prioriteit", categoryPriority: 7 };
    if (hours >= 11 && hours <= 25) return { label: "Minimaal Effect", priority: 19, category: "Lage Prioriteit", categoryPriority: 7 };
    if (hours <= 10) return { label: "Verwaarloosbaar", priority: 20, category: "Lage Prioriteit", categoryPriority: 7 };
  }

  return { label: "Niet Gecategoriseerd", priority: 99, category: "Lage Prioriteit", categoryPriority: 7 };
}

// Automation potential calculation
function calculateAutomationPotential(top3Tasks, totalHoursSaved) {
  if (top3Tasks.length === 0) return "Slecht potentieel";

  const superTasks = top3Tasks.filter(t => t.categoryPriority <= 2).length;
  const topTask = top3Tasks[0];

  if (superTasks >= 2 && totalHoursSaved >= 100) return "Extreem potentieel";

  if (superTasks >= 2 ||
      (superTasks === 1 && topTask.categoryPriority === 1 && totalHoursSaved >= 80) ||
      (top3Tasks.length === 3 && top3Tasks[2].categoryPriority <= 3)) {
    return "Heel hoog potentieel";
  }

  if (superTasks >= 1 ||
      (topTask.categoryPriority <= 3 && totalHoursSaved >= 60) ||
      (top3Tasks.length === 3 && top3Tasks[2].categoryPriority <= 4)) {
    return "Hoog potentieel";
  }

  if (topTask.categoryPriority <= 4 || totalHoursSaved >= 40) return "Redelijk potentieel";

  return "Slecht potentieel";
}

// Process all tasks
const mergedTasks = webhookData.tasks.map(task => {
  let finalTask = { ...task };

  const taskNameToCheck = task.taskName.toLowerCase();
  const llmTaskName = llmResult.task_name?.toLowerCase();

  if (taskNameToCheck === llmTaskName) {
    if (task.isAndersTask) {
      const llmScore = llmResult.automation_score;

      if (llmScore !== null && llmScore > 0) {
        let calculatedScore = Math.pow(llmScore, 2) * task.hoursMultiplier;
        if (task.isPriority) calculatedScore *= 1.2;

        const savingPercentage = savingsPercentages[llmScore] || 0.7;
        const potentialSaving = Math.round(task.hours * savingPercentage);
        const actualSavingPercentage = Math.round(savingPercentage * 100);

        finalTask = {
          ...task,
          automationScore: llmScore,
          score: calculatedScore,
          potentialSaving: potentialSaving,
          savingPercentage: actualSavingPercentage,
          llmCategory: llmResult.category,
          llmReasoning: llmResult.reasoning,
          llmWarnings: llmResult.warnings || [],
          isAutomatiseerbaar: true
        };
      } else {
        finalTask = {
          ...task,
          automationScore: llmScore || 0,
          score: 0,
          potentialSaving: 0,
          savingPercentage: 0,
          llmCategory: llmResult.category,
          llmReasoning: llmResult.reasoning,
          llmWarnings: llmResult.warnings || [],
          isAutomatiseerbaar: false
        };
      }
    } else if (task.isPriority) {
      finalTask = { ...finalTask, llmFeedback: llmResult.reasoning };
    }
  }

  if (finalTask.automationScore && finalTask.automationScore > 0 && !finalTask.isAndersTask) {
    const savingPercentage = savingsPercentages[finalTask.automationScore] || 0.7;
    const potentialSaving = Math.round(finalTask.hours * savingPercentage);
    const actualSavingPercentage = Math.round(savingPercentage * 100);

    finalTask = {
      ...finalTask,
      potentialSaving: potentialSaving,
      savingPercentage: actualSavingPercentage,
      isAutomatiseerbaar: true
    };
  }

  if (finalTask.score > 0) {
    const labelData = getTaskLabel(finalTask.automationScore, finalTask.hours, finalTask.score);
    finalTask = {
      ...finalTask,
      taskLabel: labelData.label,
      taskPriority: labelData.priority,
      taskCategory: labelData.category,
      categoryPriority: labelData.categoryPriority
    };
  }

  return finalTask;
});

const sortedTasks = mergedTasks.sort((a, b) => {
  if (a.score === null || a.score === 0) return 1;
  if (b.score === null || b.score === 0) return -1;
  return b.score - a.score;
});

const rankedTasks = sortedTasks.map((task, index) => ({
  ...task,
  rank: index + 1
}));

const top3Tasks = rankedTasks.filter(task => task.score > 0).slice(0, 3);
const priorityTask = rankedTasks.find(task => task.isPriority);
const priorityInTop3 = top3Tasks.some(task => task.taskName === priorityTask?.taskName);
const totalPotentialSaving = rankedTasks.filter(task => task.potentialSaving > 0).reduce((sum, task) => sum + task.potentialSaving, 0);

return {
  session_id: webhookData.session_id,
  bedrijfsnaam: webhookData.bedrijfsnaam,
  branche: webhookData.branche,
  bedrijfs_omschrijving: webhookData.bedrijfs_omschrijving,
  team_size: webhookData.team_size,
  source_type: webhookData.source_type,
  total_hours: webhookData.total_hours,
  systems_count: webhookData.systems_count,
  ergernissen: webhookData.ergernissen,
  obstacles: webhookData.obstacles,
  fire_percentage: webhookData.fire_percentage,
  missed_revenue: webhookData.missed_revenue,
  automation_potential: calculateAutomationPotential(top3Tasks, totalPotentialSaving),
  anders_tasks: webhookData.anders_tasks || [],
  anders_ergernissen: webhookData.anders_ergernissen || [],
  anders_obstacles: webhookData.anders_obstacles || [],
  all_tasks: rankedTasks,
  top_3_tasks: top3Tasks,
  priority_task_info: {
    task_name: priorityTask?.taskName,
    reason: webhookData.priority_reason,
    in_top_3: priorityInTop3,
    rank: priorityTask?.rank,
    score: priorityTask?.score,
    saving_percentage: priorityTask?.savingPercentage,
    label: priorityTask?.taskLabel
  },
  summary: {
    total_automatiseerbare_taken: rankedTasks.filter(t => t.score > 0).length,
    total_potential_hours_saved: totalPotentialSaving,
    percentage_time_saved: Math.round((totalPotentialSaving / webhookData.total_hours) * 100),
    niet_automatiseerbare_taken: rankedTasks.filter(t => t.score === 0).map(t => t.taskName),
    gemiddelde_besparing_percentage: Math.round(
      rankedTasks.filter(t => t.savingPercentage > 0).reduce((sum, t) => sum + t.savingPercentage, 0) / 
      rankedTasks.filter(t => t.savingPercentage > 0).length
    ) || 0
  },
  created_at: webhookData.created_at
};

