// n8n Code node - Automatiseringsrapport Generator v2
const input = $input.first().json;

// OpenAI response uitpakken
let llmResponse;
let data;
let llmContent;

try {
  if (Array.isArray(input)) {
    llmResponse = input[0].choices[0].message.content;
  } else if (input.choices) {
    llmResponse = input.choices[0].message.content;
  } else {
    llmResponse = input;
  }

  if (typeof llmResponse === 'string') {
    llmResponse = JSON.parse(llmResponse);
  }

  data = llmResponse.original_data;
  llmContent = llmResponse.rapport_content;

  if (!data || !llmContent) {
    throw new Error("Missende data of rapport_content in LLM response");
  }

} catch (error) {
  console.error("Error parsing LLM response:", error);
  throw new Error(`Kan LLM response niet verwerken: ${error.message}`);
}

// Helper functie voor task items met ge√Øntegreerde feedback
function createTaskItem(task, explanation = null) {
  return `
    <div class="task-wrapper ${explanation ? 'with-feedback' : ''}">
      <div class="task-item">
        <div class="task-info">
          <div class="task-name">${task.taskEmoji || ''} ${task.taskName}</div>
          ${task.taskLabel ? `<div class="task-label">${task.taskEmoji || ''} ${task.taskLabel}</div>` : ''}
          <div class="task-hours">Nu: ${task.hours} uur/maand ‚Üí Straks: ${task.hours - task.potentialSaving} uur/maand</div>
        </div>
        <div class="task-saving">
          <div class="saving-amount">-${task.potentialSaving} uur</div>
          <div class="efficiency">${task.savingPercentage}% effici√´nter</div>
        </div>
      </div>
      ${explanation ? `
        <div class="feedback-box">
          <span class="feedback-icon">üí°</span>
          <span class="feedback-text">${explanation}</span>
        </div>
      ` : ''}
    </div>
  `;
}

// Helper voor staafdiagram
function createBarChart(tasks) {
  if (!tasks || tasks.length === 0) return '';

  const maxHours = Math.max(...tasks.map(t => t.potentialSaving || 0));
  const bars = tasks.map(task => {
    const height = (task.potentialSaving / maxHours) * 80;
    return `
      <div class="bar" style="height: ${height}%;">
        <div class="bar-value">${task.potentialSaving} uur</div>
        <div class="bar-label">${task.taskName}</div>
      </div>
    `;
  }).join('');

  return `
    <div class="chart-container">
      ${bars}
    </div>
  `;
}

// Helper voor secties
function createSection(title, content, condition = true) {
  if (!condition || !content) return '';

  return `
    <div class="section">
      <h2 class="section-title">${title}</h2>
      ${content}
    </div>
  `;
}

