  `;

  html += `
      </div>
    </body>
    </html>
  `;

  return html;
}

// Genereer HTML
const html = generateDynamicHTML(data, llmContent);

// Return voor volgende node
return {
  html: html,
  filename: `rapport-${data.contact_name || 'klant'}-${Date.now()}.pdf`,
  contact_name: data.contact_name,
  email: data.email || null
};

